def l_array(array)
    puts "Voici le nombre de journaliste dans le tableau : #{array.length}"
    return array.length
end


def h_shortest(array)
   i = 0.to_i
   while true
      array.each do |valeur|
        if (valeur.size == i)
        puts " Voici la valeur la plus courte du tableau, HEYYYY c'est pas la taille qui compte ! : #{valeur}"
        return valeur
        end
      end
  i = i + 1
    end 
 end

 def five_car(array,nbcar)
    tab = []
    array.each do |value|
        if (value[1..-1].size == nbcar)
            tab << value
        end
    end 
    puts "Il y a #{tab.count} valeur dans le tableau ayant #{nbcar} caractères"
    return tab.count
end

def majletter(array)
    i = 0
    array.each do |value|
        maj = value[1].scan(/[A-Z]/).length
        if maj > 0
            i = i + 1
        end
    end
    puts "Il y a #{i} Handle qui commencent par une majuscule, ça fait beaucoup là non ??????"
 return i
end 

def alpha_rank(array)
    puts "Voici les elements du tableau par ordre aphabetique, quand c'est rangé c'est mieux non ? : "
    puts array.sort
end 

def sortby_size(array)
   puts "Voici les elements du tableau du plus petit (on sait deja qui c'est) au plus grand SUSPEEEENS !!! :"
   puts array.sort_by(&:length)
   puts ""
   puts "Oh putainnn oui il est long celui la !!!!!"
end


def position_of(array,position)
    puts "La position de #{position} est #{array.index(position)}"
    return array.index(position)
end

def repartition(array,nbcar)
    i = 0
    tab = []
      while i != array.length  
        array.each do |value| 
        (value[1..-1].size == nbcar)
            tab << value
        end
        i = i + 1
      end
    
    puts "Il y a #{tab.count} valeur dans le tableau ayant #{nbcar} caractères"
    return tab.count
end




def perform(array)
   l_array(array)
   h_shortest(array)
   five_car(array,5)
   majletter(array)
   alpha_rank(array)
   sortby_size(array)
   position_of(array,"@epenser")
   repartition(array,array.length)
end

